  // Doubles the size of the array if it is full.    private void ensureCapacity()    {        if(this.numberOfEntries >= this.arrayOfSetEntries.length){            int newLength = 2 * this.arrayOfSetEntries.length;            this.arrayOfSetEntries = Arrays.copyOf(this.arrayOfSetEntries, newLength);        }        //TODO Project2    } // end ensureCapacity    /**     * Retrieves all entries that are in this array.     *     * @return A newly allocated array of all the entries.     */    public T[] toArray()    {        T[] result = (T[]) new Comparable<?>[this.numberOfEntries]; // Unchecked cast        for (int index = 0; index < this.numberOfEntries; index++)        {            result[index] = this.arrayOfSetEntries[index];        }        return result;        //TODO Project2    } // end toArray    /**     * Sees whether this array is empty.     *     * @return True if this array is empty, or false if not.     */    public boolean isEmpty()    {        //TODO Project2        return this.numberOfEntries == 0;    } // end isEmpty    /**     * Gets the number of entries currently in this array.     *     * @return The integer number of entries currently in the array.     */    public int getCurrentSize()    {        //TODO Project2        return this.numberOfEntries;    } // end getCurrentSize    /**     * Tests whether this array contains a given entry.     *     * @param anEntry The entry to locate.     * @return True if the array contains anEntry, or false if not.     */    public boolean contains(T anEntry)    {        boolean result = false;        for (int i = 0; !result && i < this.numberOfEntries ; i++) {            if(anEntry.equals(this.arrayOfSetEntries[i])){                result = true;            }        }        return result;        //TODO Project2    } // end contains    /**     * Removes all entries from this array.     */    public void clear()    {        while (!isEmpty())            remove();        //TODO Project2    } // end clear    /**     * Removes one unspecified entry from this bag.     *     * @return Either the removed entry if the removal     * was successful, or null if not.     */    public T remove()    {        T entry = null;        if(this.numberOfEntries > 0) {            entry = this.arrayOfSetEntries[this.numberOfEntries - 1];            this.numberOfEntries--;        }        return entry;        //TODO Project2    } // end remove    /**     * Removes one occurrence of a given entry from this array.     *     * @param anEntry The entry to be removed.     * @return True if the removal was successful, or null if not.     */    public boolean removeElement(T anEntry)    {        boolean result = false;        for (int i = 0; i < this.numberOfEntries ; i++) {            if(anEntry.equals(this.arrayOfSetEntries[i]) && !result){                removeEntry(i);                result = true;            }        }        return result;        //TODO Project2    } // end removeElement    // Removes and returns the array entry at a given index.    // If no such entry exists, returns null.    private T removeEntry(int givenIndex)    {        T removedObject = null;        if(this.arrayOfSetEntries[givenIndex] != null) {            removedObject = this.arrayOfSetEntries[givenIndex];            this.arrayOfSetEntries[givenIndex] = this.arrayOfSetEntries[this.numberOfEntries - 1];            this.arrayOfSetEntries[this.numberOfEntries] = null;            this.numberOfEntries--;        }        return removedObject;        //TODO Project2    } // end removeEntry    // Displays a set.    public void displaySet()    {       if(!isEmpty()){           System.out.print("There are " + this.numberOfEntries + " element(s): ");           for (int i = 0; i < this.numberOfEntries; i++) {                System.out.print(this.arrayOfSetEntries[i] + " ") ;           }           System.out.println();       }        //TODO Project2    } // end displaySet    public static void main(String[] args)    {        String[] inputData = {"A", "B", "C", "D", "A", "C", "B", "B"};        System.out.println("Creating aSet with the secondary constructor - capacity of " + inputData.length);        SetInterface<String> aSet = new ArraySetWithArray<>(inputData.length);        System.out.println("Adding elements from inputData to aSet");        for (int i = 0; i < inputData.length; i++)            aSet.add(inputData[i]);        aSet.displaySet();        System.out.println("\nClearing aSet");        aSet.clear();        aSet.displaySet();        System.out.println("aSet isEmpty returns " + aSet.isEmpty());        System.out.println("The size of aSet is " + aSet.getCurrentSize());        System.out.println("\nCreating set1 with default constructor");        SetInterface<String> set1 = new ArraySetWithArray<>();        // Initial capacity is 3        System.out.println("\nset1 initially empty, capacity of 3: ");        set1.displaySet();        System.out.println("\nAdding elements to set1");        set1.add("A");        set1.add("A");        set1.add("B");        set1.add("A");        set1.add("C");        set1.add("A");        System.out.println("\nset1 after adding elements: ");        set1.displaySet();        System.out.println("\nset1 after adding more elements which should trigger resizing: ");        set1.add("V");        set1.add("V");        set1.add("T");        set1.add("U");        set1.add("V");        set1.add("W");        set1.add("X");        set1.add("Y");        set1.add("Z");        set1.displaySet();        System.out.println("\nCreating set2 with the secondary constructor that takes an array as input");        SetInterface<String> set2 = new ArraySetWithArray<>(inputData);        System.out.println("Adding more elements to set2");        set2.add("A");        set2.add("B");        set2.add("B");        set2.add("A");        set2.add("C");        set2.add("C");        set2.add("D");        set2.add("E");        set2.add("F");        set2.add("G");        set2.add("H");        System.out.println("set2 after adding and resizing: ");        set2.displaySet();        System.out.println("\nset1 contains A: " + set1.contains("A"));        System.out.println("set1 contains E: " + set1.contains("E"));        set1.removeElement("B");        System.out.println("After removing B from set1:");        set1.displaySet();        System.out.println("After removing " + set1.remove()                + " from set1:");        set1.displaySet();    }} // end ArraySetWithArray